import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Weather from './components/weather';
import './App.css';
import Moment from 'react-moment';
import LoginForm from './components/LoginForm';
import { Button, Row, Col } from 'react-bootstrap';



function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if the user is already authenticated in localStorage
    const storedStatus = localStorage.getItem('isLoggedIn');
    if (storedStatus === 'true') {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    setIsLoggedIn(false);
  };

  return (
    
    <div className="App">
      {isLoggedIn ? (
        <>
          <br></br>
          <Row >
            <Col sm={9}></Col>
            <Col sm={2}><Button className="logoutButton" variant="outline-light" onClick={handleLogout}>Light Mode</Button></Col>
            <Col sm={1}><Button className="logoutButton" variant="outline-danger" onClick={handleLogout}>Logout</Button></Col>
            
            </Row>
            <h1>My Weather App</h1>
          <Row >
            <Moment local interval={1}></Moment>      
          </Row>
          <Weather/>
          
        </>
      ) : (
        <LoginForm onLogin={handleLogin} />
      )}



      
    </div>
  );
}

export default App;
